jQuery.Hashtable = function() { this.items = new Array; this.itemsCount = 0; this.add = function(a, b) { if (!this.containsKey(a)) { this.items[a] = b; this.itemsCount } else { this.items[a] = b } }; this.get = function(a) { if (this.containsKey(a)) return this.items[a]; else return null }; this.remove = function(a) { if (this.containsKey(a)) { delete this.items[a]; this.itemsCount-- } else throw "key '" + a + "' does not exists." }; this.containsKey = function(a) { return typeof this.items[a] != "undefined" }; this.containsValue = function(b) { for (var c in this.items) { if (this.items[c] == b) return true } return false }; this.contains = function(a) { return this.containsKey(a) || this.containsValue(a) }; this.clear = function() { this.items = new Array; itemsCount = 0 }; this.size = function() { return this.itemsCount }; this.isEmpty = function() { return this.size() == 0 } }